import React from 'react'
import { useEffect, useState } from 'react';
import { useParams } from 'react-router-dom';
import axios from "axios";


function Detail() {

    const { detailId } = useParams();

    const [character, setCharacter] = useState({});


    // useEffect(()=>{
    //     const URL_BASE = "https://rickandmortyapi.com/api"
    //     const API_KEY = "ef19ec2c194a.fa1ee29143e598093d2f"
    //     fetch(${URL_BASE}/character/${id}?key=${API_KEY})
    //         .then((resp)=>resp.json())
    //         .then((data)=>{
    //             (data.name)
    //             ? setCharacter(data)
    //             : window.alert('No esta esa ID')
    //         })
    // },[id])

    useEffect(() => {

        const URL_BASE = "https://be-a-rym.up.railway.app/api";
        const KEY = "d640439ec558.6d012afc549ba6662537";

        fetch(`${URL_BASE}/character/${detailId}?key=${KEY}`)
            .then((response) => response.json())
            .then((data) => {
                (data.name) ? setCharacter(data) : alert("No esta el ID")
            })


        axios(`${URL_BASE}/character/${detailId}?Key=${KEY}`)
         .then((response) => setCharacter(response.data))
        .then((data) => {
                    (data.name) ? setCharacter(data) : alert("No esta el ID")
                })

    }, [detailId]);




    return (
        <div>
            <h1>Bievenido a los detalles</h1>
            {console.log(character)}

            {

                character.name ?
                    (<>
                        <h2>{character.name}</h2>
                        <p>{character.status}</p>
                        <p>{character.species}</p>
                        <p>{character.gender}</p>
                        <p>{character.origin?.name}</p>
                        <img src={character.image} alt="img" />
                    </>)

                    : <h3>Loading...</h3>
            }


        </div>
    );
}

export default Detail;
