import {ADD_FAVORITE,REMOVE_FAVORITE,ORDER_CARDS,FILTER_CARDS} from "../redux/actions.js";

const initialState={
    allCharacters:[],
    myFavorites:[],
}

export default function Reducer(state=initialState,action){
    switch(action.type){
        case ADD_FAVORITE:
            return{
                ...state,
                myFavorites:[...state.myFavorites,action.payload]
            };
        case REMOVE_FAVORITE:
            return{
                ...state,
                myFavorites:state.myFavorites.filter(
                    (favorite)=>favorite.id !== action.payload
                ),
            };
        case FILTER_CARDS:
            return{
                ...state,
                myFavorites:state.myFavorites.filter(
                    (favorite)=>favorite.gender !== action.payload
                ),

            }
        case ORDER_CARDS:
            if(action.payload === "")
            return{
                ...state,
                myFavorites:state.myFavorites.sort(function (a, b) {
                    if (a.name > b.name) {
                      return 1;
                    }
                    if (a.name < b.name) {
                      return -1;
                    }
                    // a must be equal to b
                    return 0;
                  })
            }
        default: return {...state};
    }

}
