import React from 'react'
import { useState } from 'react';
import styles from "../../assets/styles/components/Form/Form.module.css";

const validate=(form,setErrors,errors)=>{

    const templateValidacion=/^\w+([\.-]?\w+)*@\w+([\.-]?\w+)*(\.\w{3})+$/;

    if(!form.email){
        setErrors({...errors,email:"Email Vacio"});
    }else{
        if(templateValidacion.test(form.email)){
            setErrors({...errors,email:""});
        }else{
            setErrors({...errors,email:"Email inválido"});
        }
    }
}


const Form = () => {
    //Creo el estado para el formulario
    const [form,setForm]=useState({
        email:"",
        password:""
    });

    //Creo un formulario para los errores
    const [errors,setErrors]=useState({
        email:"",
        password:""
    })

    //Funcion para enviar el email
    const submitHandler=(event)=>{
        event.preventDefault();
        alert("Login Exitoso :)");
    }

    //Esta funcion va escribiendo en tiempo real
    //los atributos del formulario

    const handleChange=(event)=>{
        const property=event.target.name;
        const value=event.target.value;

        setForm({...form, [property]:value});
        validate({...form,[property]:value},setErrors,errors);
    }

  return (
    <div>
      <form onSubmit={submitHandler}>
        <div>
            <label htmlFor="email">Email:</label>
            <input 
            type="text" 
            name="email" 
            value={form.email} 
            onChange={handleChange}
            className={errors.email? styles.error : styles.success}
            />
            <span className={styles.error}>{errors.email}</span>
        </div>
        <div>
            <label htmlFor="password">Password:</label>
            <input 
            type="text" 
            name='password' 
            value={form.password}
            onChange={handleChange}
            />
        </div>
        <button type='submit'>Login</button>
      </form>
    </div>
  )
}

export default Form
