import React from 'react'
import { useSelector } from 'react-redux';
import { useDispatch } from 'react';
import Card from '../Card/Card';
import styles from "../../assets/styles/components/Favorite/Favorite.module.css";
import { orderCards } from "../../redux/actions";

const Favorite = () => {
  const favorites = useSelector(state => state.myFavorites);
  // const dispatch = useDispatch();

{/* <button onClick={()=> dispatch(actions.deleteCelular(props.id))}>x</button> */}
  // onChangeCapture={(event) => { return dispatch(orderCards(event.target.value)) }}

  return (
    <div>
      <h1 className={styles.title}>Mis favoritos</h1>
      <div className={styles.contentOption}>
        <select name="" id="" className={styles.optionOrder} onClick={()=> dispatch(actions.orderCards(props.id))} >

          <option value="Ascendente">Ascendente</option>
          <option value="Descendente">Descendente</option>

        </select>

        <select name="" id="" className={styles.optionGender}>

          <option value="Male">Male</option>
          <option value="Female">Female</option>
          <option value="Genderless">Genderless</option>
          <option value="unknown">unknown</option>

        </select>

      </div>
      <div className={styles.contentCard}>
        {favorites.map(({ id, name, species, gender, image }) => {
          return <Card id={id} name={name} species={species} gender={gender} image={image} />
        })}
      </div>
    </div>
  )
}

export default Favorite;
