import React from 'react'
import { useState } from 'react';
import styles from "../../assets/styles/components/Form/Form.module.css";
import validation from './validation';

const validateEmail=(form,setErrors,errors)=>{

    if(!form.email){
        setErrors({...errors,email:"Email Vacio"});
    }else{
        if(form.email.length < 35){
            if(/^\w+([\.-]?\w+)*@\w+([\.-]?\w+)*(\.\w{3})+$/.test(form.email)){
                setErrors({...errors,email:""});
            }else{
                setErrors({...errors,email:"Email inválido"});
            }
        }else{
            setErrors({...errors,email:"Supera los 35 caracteres"});
        }
        
    }
}

const validatePass=(form,setErrors,errors)=>{
    if(!form.password) setErrors({...errors,password:"Password vacio"})
    if(form.password.length < 6 || form.password.length > 10){
        setErrors({...errors,password:"Debe tener entre 6 y 10 caracteres"})
    }else{
        if(!form.password.match(/\d/)){
            setErrors({...errors,password:"Debe tener al menos un numero"})
        }else{
            setErrors({...errors,password:""})
        }
    }
}


const Form = () => {
    //Creo el estado para el formulario
    const [form,setForm]=useState({
        email:"",
        password:""
    });

    //Creo un formulario para los errores
    const [errors,setErrors]=useState({
        email:"",
        password:""
    })

    //Funcion para enviar el email
    const submitHandler=(event)=>{
        event.preventDefault();
        if(form.email !== "" && form.password !== ""){
            alert("Login Exitoso :)");
            setForm({...form,email:"",password:""});
        }else{
            setErrors({...errors,password:"todos los campos son obligatorios"})
        }
        setTimeout(()=>{
            setErrors({...errors,password:""})
        },2000)
        

    }

    //Esta funcion va escribiendo en tiempo real
    //los atributos del formulario en el estado

    const handleChange=(event)=>{
        const property=event.target.name;
        const value=event.target.value;

        setForm({...form, [property]:value});
        // validation({...form, [property]:value},errors,setErrors);
        if(property === "email"){
            validateEmail({...form,[property]:value},setErrors,errors);
        }else{
            validatePass({...form,[property]:value},setErrors,errors);
        }
        
        
    }

  return (
    <div>
      <form onSubmit={submitHandler}>
        <div>
            <label htmlFor="email">Email:</label>
            <input 
            type="text" 
            name="email" 
            value={form.email} 
            onChange={handleChange}
            className={errors.email? styles.error : styles.success}
            />
            <span className={styles.error}>{errors.email}</span>
        </div>
        <div>
            <label htmlFor="password">Password:</label>
            <input 
            type="text" 
            name='password' 
            value={form.password}
            onChange={handleChange}
            />
            <span className={styles.error}>{errors.password}</span>
        </div>
        <button type='submit'>Login</button>
      </form>
    </div>
  )
}

export default Form
