import { ADD_FAVORITE, REMOVE_FAVORITE, ORDER_CARDS, FILTER_CARDS,ALL_FAVORITES } from "../redux/actions.js";

const initialState = {
    allCharacters: [],
    myFavorites: [],
}

export default function Reducer(state = initialState, action) {
    switch (action.type) {
        case ALL_FAVORITES:
            return{
                ...state,
                myFavorites:state.myFavorites
            }
        case ADD_FAVORITE:
            return {
                ...state,
                myFavorites: [...state.myFavorites, action.payload]
            };
        case REMOVE_FAVORITE:
            return {
                ...state,
                myFavorites: state.myFavorites.filter(
                    (favorite) => favorite.id !== action.payload
                ),
            };
        case FILTER_CARDS:
            return {
                ...state,
                myFavorites: state.myFavorites.filter(
                    (favorite) => favorite.gender !== action.payload
                ),

            }
        case ORDER_CARDS:
            return {

                ...state,
                myFavorites: ordenarArray(action.payload, state.myFavorites)
                
            }


        default: return { ...state };
    }

}



const ordenarArray = (tipo, array) => {

    if (tipo === "Ascendente") {

        for (let i = 0; i < array.length; i++) {
            var miIndex = i;
            for (let j = i + 1; j < array.length; j++) {
                if (array[j].id < array[miIndex].id) miIndex = j;
            }
            var aux = array[i];
            array[i] = array[miIndex];
            array[miIndex] = aux;
        }
        
        return array;

    } else {
        for (let i = 0; i < array.length; i++) {
            var miIndex = i;
            for (let j = i + 1; j < array.length; j++) {
                if (array[j].id > array[miIndex].id) miIndex = j;
            }
            var aux = array[i];
            array[i] = array[miIndex];
            array[miIndex] = aux;
        }
        return array;
    }
}
