import React from 'react'
import { useSelector,useDispatch, connect  } from 'react-redux';
import { useEffect,useState} from 'react';
import Card from '../Card/Card';
import styles from "../../assets/styles/components/Favorite/Favorite.module.css";
import { filterCards, orderCards,allFavorites } from "../../redux/actions";
// import { mapDispatchToProps, mapStateToProps } from "react-redux";


const Favorite = () => {
  
  const favorites=useSelector(state=>state.myFavorites);

  const [selec,setSelec]=useState();

  const dispatch = useDispatch();

  const handleChangeSelect=(event)=>{
    if(event.target.value != ""){
      if(event.target.value === "Ascendente"){
      
        setSelec("Descendente");
      }else{
        
        setSelec("Ascendente");
      }
    }else{
      setSelec("Ascendente");
    }
    
  }

  // const  ordenar=(event)=>{ dispatch(orderCards(event.target.value))}

  useEffect(() => {

    dispatch(orderCards(selec))
    
    
  }, [selec]);

  // console.log(favorites);
  // onClick={(event)=>{ dispatch(orderCards(event.target.value))}}
  
  // onClick={(event)=>{console.log(event.target.value)}} 
  
  
  return (
    <div>
      <h1 className={styles.title}>Mis favoritos</h1>
      <div className={styles.contentOption}>
        <select name="" id="" className={styles.optionOrder} onChange={handleChangeSelect}  >

          <option value="Ascendente">Ascendente</option>
          <option value="Descendente">Descendente</option>

        </select>

        <select name="" id="" className={styles.optionGender}>

          <option value="Male">Male</option>
          <option value="Female">Female</option>
          <option value="Genderless">Genderless</option>
          <option value="unknown">unknown</option>

        </select>

      </div>
      <div className={styles.contentCard}>
        {favorites.map(({ id, name, species, gender, image }) => {
          return <Card key={id} id={id} name={name} species={species} gender={gender} image={image} />
        })}
      </div>
    </div>
  )
}
export default Favorite;

